import javax.servlet.http.*;
import javax.servlet.*;
import java.io.*;

public class LoginServlet extends HttpServlet {

    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        response.setContentType("text/html");
        PrintWriter out = response.getWriter();

        String email = request.getParameter("email");
        String password = request.getParameter("password");

        if(validateInput(email, password)){
            HttpSession session=request.getSession();  
            session.setAttribute("email",email);
            session.setMaxInactiveInterval(30*60);
            out.print("You are successfully logged in!");
            out.print("<br>Welcome, "+email);
            // redirect to user home page
        } else {
            out.print("<p style='color:red;'>Invalid email or password. Password must be at least 8 characters long, contain at least one uppercase letter, one lowercase letter, one number, and one special character.</p>");
            request.getRequestDispatcher("index.html").include(request, response);
        }
        out.close();
    }

    private boolean validateInput(String email, String password){
        String emailRegex = "^(.+)@(.+)$";
        String passwordRegex = "^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=])(?=\\S+$).{8,}$";

        return email.matches(emailRegex) && password.matches(passwordRegex);
    }
}
