import java.util.Collections;
import java.util.List;
import java.util.Arrays;

public class SortingDescending {
    public static void main(String[] args) {
        List<Integer> numbers = Arrays.asList(4, 2, 9, 6, 23, 12);

        Collections.sort(numbers); // Sorts the array list in ascending order

        Collections.reverse(numbers); // Reverses the ordered list to get descending order

        System.out.println(numbers);
    }
}
